package TestPackage1;

public class DecreIncreOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=40;
		
		System.out.println(a);
		
		System.out.println(a++);
		System.out.println(++a);

        System.out.println(a);
		
		System.out.println(a--);
		System.out.println(--a);
	}

}
